riot.tag2('device-ready', '<p class="event listening blink" hide="{received}">Connecting to Device</p> <p class="event received" show="{received}">Device is Ready</p>', '', 'id="deviceready"', function(opts) {
        var tag = this;
        tag.received = false;
        console.log('The script is loading!');
        tag.deviceReady = {

            initialize: function() {
                document.addEventListener('deviceready', this.onDeviceReady.bind(this), false);
            },

            onDeviceReady: function() {
                this.receivedEvent();
            },

            receivedEvent: function() {
                tag.received = true;
                tag.update();

                console.log('Received Event!');
            }
        };

        tag.deviceReady.initialize();
});
